package test;

public class VariblesTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		System.out.println(i);
		System.out.println("i");
		
		i=20;
		System.out.println(i);
		int j=10;
		i=i+j;
		System.out.println(i);
		i=i*2;
		System.out.println(i);
		System.out.println(i++);
		System.out.println(i);
		System.out.println(++i);
		System.out.println(i);
		i=i++;
		System.out.println(i);
		//double
		
		double a =10.5;
		System.out.println(a);
		
		//char
		char p='x';
		System.out.println(p);
		System.out.println('p');
		
		//boolean
		boolean b1 = true;
		boolean b2 = false;
		System.out.println(b1);
		System.out.println(b2);
		b1 = false;
		System.out.println(b1);



	}

}
